# Template of a half(triangular matrix) -----------------------------------
#combos <- combn(ncol(ppmi.raw.data),2) # combinations without repetitions


# Template of a full (square) matrix -> recommended option ----------------
combos <- expand.grid(rep(list(1:ncol(ppmi.raw.data)), 2 )) # combinations with repetitions, so (1,1)(2,1)(3,1)...(6,1)(1,2)(2,2)(3,2)...
combos <- as.matrix(combos)
combos <- t(combos) # transpose matrix

mat1 <- adply(combos, 2, function(x) {
  test <- chisq.test(ppmi.raw.data[, x[1]], ppmi.raw.data[, x[2]])
  print("yay")
  out <- data.frame("Row" = colnames(ppmi.raw.data)[x[1]]
                    , "Column" = colnames(ppmi.raw.data[x[2]])
                    , "Chi.Square" = round(test$statistic,3)
                    ,"df"= test$parameter
                    ,"p.value" = round(test$p.value, 3)
  )
  return(out)
})

